<template>
	<view class="content">
		<view class="login_wrap">
			<image class="icon" :src="img_src"></image>
			<view class="input_group">
				<view class="input_item">
					<text>账号</text>
					<input type="text" placeholder="在此输入你的账号" v-model="id" />
				</view>
				<view class="input_item">
					<text>密码</text>
					<input type="password" placeholder="在此输入你的密码" v-model="password" />
				</view>
				<view class="input_btn" @tap="login()">
					<text>登录</text>
				</view>
			</view>
		</view>
	</view>
</template>

<script>
	export default {
		data() {
			return {
				img_src: 'data:image/jpeg;base64,',
				id:'',
				password:''
			}
		},
		onLoad(Option) {
			if(Option.request == 'reload') {
				uni.switchTab({
					url: '../user/user'
				})
			}
		},
		methods: {
			login() {
				if(this.id == '') {
					uni.showToast({
						title: '账号没填哦！',
						icon: 'none'
					})
				} else {
					if (this.password == '') {
						uni.showToast({
							title: '密码没填哦！',
							icon: 'none'
						})
					} else {
						let info = uni.getStorageSync('user_info');
						if(this.id != info.id || this.password != info.password) {
							uni.showToast({
								title: '信息错误！',
								icon: 'none'
							})
							this.id = '';
							this.password = '';
						} else {
							uni.setStorageSync('login',true);
							uni.showToast({
								title: '登陆成功！',
								icon: 'none'
							})
							uni.switchTab({
								url: '../user/user'
							})
						}
					}
				}
			}
		}
	}
</script>

